import React from 'react'
import { createRoot } from 'react-dom/client'

import { SinglePlayer } from './index'




const PlayerCard = ({players, toggle}) => {
    console.log(players)
    const some = () => {
        return (

            <>
            <div id='single-player-view'>
                {players.map((player, index) => {
                    return (
                        <div className = 'single-player-card' key={player.name + index}> 
                        <div className="header-info" >
                        <p className='pup-title'>{player.name}</p>
                        <p className='pup-number'>{player.id}</p>
                        </div>
                        <p>{player.status}</p>
                        <p>{player.breed}</p>
                        <img src={player.imageUrl} alt="" />
                        <button id="see-all" onClick={PlayerCard}>Back to all players</button>
                        </div>
                    )
                })}
            </div>
            </>)
    }
    try {
        return (
            <>
            <div id='all-players-container'>
            {players.map((player, index) => {
                return (
                <div className='single-player-card'  key={player.breed + index} >
                    <div className='header-info'>
                        <p className='pup-title'>{player.name}</p>
                        <p className='pup-number'>{player.id}</p>
                     </div>
                <img src={player.imageUrl} alt="" />
                <button id="see-details-bttn" type="button" onClick={some}>See Details</button>
            </div>
            ) 
            })}
            </div>
            </>
        )
    } catch (error) {
        console.log('error');
    }
}




export default PlayerCard;












