import React, {useState, useEffect} from 'react'
import { Players, PlayerForm, SinglePlayer } from './index'
import { fetchAllPlayers, fetchSinglePlayer } from '../ajaxHelpers';


const App = () => {
    //have some state here
    const [players, setPlayers] = useState([]);
    // const [singlePlayer, setSinglePlayer] = useState([]);

    //getAllPlayers function to be used in useEffect because it needs async/await
    const getAllPlayers = async () => {
        const playerList = await fetchAllPlayers();
        console.log(playerList);
        playerList.forEach(player => console.log(player.id));
        setPlayers(playerList);
    }

    //getSinglePlayer function to be used in useEffect because it needs async/await
    const getSinglePlayer = async (playerId) => {
        const singlePlayer = await fetchSinglePlayer(playerId);
        console.log(`this is the single player: ${singlePlayer}`);
        setSinglePlayer(singlePlayer);
    }

    //useEffect to get initial players
    useEffect(() => {
        getAllPlayers();
        getSinglePlayer(players.id);
    }, [])

    return (
    <>
        <PlayerForm />
        <Players players={players} />
        {/* <SinglePlayer singlePlayer={singlePlayer} /> */}
    </>
    )
}

export default App
