import React from 'react'


const PlayerCard = ({players}) => {
//console.log(players);
let playerContainerHTML = '';
players.map((player) => {
    console.log(player);
    let playerHTML = `
    <div class='single-player-card'>
    <div class='header-info'>
        <p class='pup-title'>${player.name}</p>
        <p class='pup-number'>${player.id}</p>
    </div>
    <img src="${player.imageUrl}" alt="" />
    <button type="button" onClick='click me!'>See Details</button>
    </div>

    `;

   playerContainerHTML += playerHTML;

})
document.getElementById('all-players-container').innerHTML = playerContainerHTML;
}


export default PlayerCard;
