import React, {useState, useEffect} from 'react'
import { Players, PlayerForm, PlayerCard, SinglePlayer } from './index'
import { fetchAllPlayers } from '../ajaxHelpers';


const App = () => {
    //have some state here
    const [players, setPlayers] = useState([]);
    const [singlePlayer, setSinglePlayer] = useState([]);
    const [toggle, setToggle] = useState(false);

    //getAllPlayers function to be used in useEffect because it needs async/await
    const getAllPlayers = async () => {
        const playerList = await fetchAllPlayers();
        //console.log(playerList);
        //playerList.forEach(player => console.log(player.id));
        setPlayers(playerList);
    }

    //useEffect to get initial players
    useEffect(() => {
        getAllPlayers();

    }, [])

    return (
    <>
        <PlayerForm />
        <Players players={players} />
        <PlayerCard players={players} />
        <SinglePlayer players={players} />
    </>
    )
}

export default App






