import React, {useState, useEffect} from 'react'
import { Players, PlayerForm, PlayerCard, SinglePlayer } from './index'
import { fetchAllPlayers, fetchSinglePlayer } from '../ajaxHelpers';


const App = () => {
    //have some state here
    const [players, setPlayers] = useState([]);
    const [selectedPlayer, setSelectedPlayer] = useState([]);
    // const [toggle, setToggle] = useState(false);

    // {
    //     players: [{id: 2 name: crumpet}{...}{...}],
    //     selectedPlayer: {...},
    // }

    //getAllPlayers function to be used in useEffect because it needs async/await
    const getAllPlayers = async () => {
        const players = await fetchAllPlayers();
        setPlayers(players);

        // setToggle(false);
    }

    const getSinglePlayer = async ({players}) => {
        //for each players object, return the single player whos player matches the given id
        console.log(players);
        const player = await fetchSinglePlayer(players.id);
        setSinglePlayer(player)
    }

    //useEffect to get initial players
    useEffect(() => {
        getAllPlayers();   
        getSinglePlayer();    
    }, [])

    // const toggleFunc = () => {
    //     if (toggle === true) {
    //         <SinglePlayer player={players}/>}
    //     else {
    //         <PlayerCard players={players}/>}
    //     }

    return (
    <>
        <PlayerForm />
        {/* <Players players={players} /> */}
        <PlayerCard players={players} />
        {/* <SinglePlayer players={players} />  */}

    </>
    )
}

export default App


